import React from 'react'
import {connect} from 'react-redux'
import {startClock, serverRenderClock,getGoldList,decrementCount,Axios} from '../store'
import Examples from '../components/examples'
import LayoutA from '../index.js'
import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import './index.css'
const { Header, Content, Footer } = Layout;

class Index extends React.Component {
    static getInitialProps ({ reduxStore, req }) {
    const isServer = !!req
    reduxStore.dispatch(serverRenderClock(isServer))
    const GET_LIST = 'INDEX/GET_GOLD_LIST'
    const changGoldList = list =>({
      type:GET_LIST,
      list
    })
    return Axios.post('https://extension-ms.juejin.im/resources/gold',{
      category: "frontend",
      limit: 30,
      offset: 0,
      order: "time"
    })
    .then(res=>{
         const {data} = res.data
         reduxStore.dispatch(changGoldList(data))
        
         return  { GOLD_LIST: (reduxStore.getState())['GOLD_LIST']}
    })  
  }

  componentDidMount () {
    const {dispatch,GOLD_LIST} = this.props
    console.log(GOLD_LIST)
    this.timer = startClock(dispatch)
    // dispatch(getGoldList({
    //   category: "frontend",
    //   limit: 30,
    //   offset: 0,
    //   order: "time"
    // }))
  }

  componentWillUnmount () {
    clearInterval(this.timer)
  }

  render () {
   const {GOLD_LIST,state} = this.props;
   console.log(GOLD_LIST)
   console.log(state)
    return (
 
          <Layout id="components-layout-demo-fixed">
           
          <Header style={{ position: 'fixed', zIndex: 1, width: '100%' }}>
                <div className="logo" />
                <Menu
                    theme="dark"
                    mode="horizontal"
                    defaultSelectedKeys={['2']}
                    style={{ lineHeight: '64px' }}
                >
                    <Menu.Item key="1">nav 1</Menu.Item>
                    <Menu.Item key="2">nav 2</Menu.Item>
                    <Menu.Item key="3">nav 3</Menu.Item>
                </Menu>
            </Header> 
            <div  className="main-area">
              <div className="source gold-source">
                  <div className="entry-list">
                  <Examples/>
                  
                    <ul className="list">
                        <li className="item" >11
                          <a className="item-content">
                          11
                          </a>
                        </li>
                        {GOLD_LIST.map(item=>{
                           return <li className="item">
                               <a className="item-content">
                                  <div className="badge"   title="至少已有 25 人喜欢">
                                    <div className="icon ion-arrow-up-b" ></div>
                                    <div className="text">25</div>
                                  </div>
                                  <div className="entry-info">
                                    <div className="title"  title={item.title}>{item.title}</div>
                                    <div className="meta"  >
                                          <div className="list"  >
                                              <div className="meta-item">
                                                <span className="text" title="Fri, 03 Jan 2020 14:02:46 GMT">2小时前</span>
                                              </div>
                                              <div className="meta-item">
                        <span className="text" title="`访问${item.username}的主页`">{item.username}</span>
                                              </div>
                                          </div>
                                      </div>
                                  </div>
                                </a> { item.title}
                           </li>
                        })}
                    </ul>
                  </div>
              </div>
              <div className="source other-source">
                  <div className="entry-list">
                      <ul className="list">
                          <li className="item-row">
                              33
                           </li>
                      </ul>
                  </div>
              </div>
            </div>
    
            <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer>                                                                
          </Layout>
      
    )
  }
}

export default connect( 
  state => ({ GOLD_LIST: state.GOLD_LIST,state })
 
)(Index)
