import React from 'react'
import {connect} from 'react-redux'
import {startClock, serverRenderClock,getGoldList,changGoldList,changGitList,Axios} from '../store'
import Examples from '../components/examples'
import LayoutA from '../index.js'
import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import './index.css'
const { Header, Content, Footer } = Layout;
let getlist = (url,param) => {
  return new Promise((resolve, reject) => {
    Axios.post(url,param)
    .then(res=>{
         const {data} = res.data
         resolve(data)
    })  
  })
}

class Index extends React.Component {
    static getInitialProps ({ reduxStore, req }) {
    const isServer = !!req
    reduxStore.dispatch(serverRenderClock(isServer))
 
    let  p1=getlist('https://extension-ms.juejin.im/resources/gold',{
      category: "frontend",
      limit: 30,
      offset: 0,
      order: "time"
    })
    let  p2=getlist('https://extension-ms.juejin.im/resources/github',{
      "category":"upcome",
      "period":"day",
      "lang":"javascript",
      "offset":0,
      "limit":30
    })
    return Promise.all([p1, p2]).then((result) => {
                    //['成功了', 'success']
                    reduxStore.dispatch(changGoldList(result[0]))
                    reduxStore.dispatch(changGitList(result[1]))
                    return  {
                       GOLD_LIST: (reduxStore.getState())['GOLD_LIST'],
                       GIT_LIST: (reduxStore.getState())['GIT_LIST']
                      }
    }).catch((error) => {
      console.log(error)
    })
    return Axios.post('https://extension-ms.juejin.im/resources/gold',{
      category: "frontend",
      limit: 30,
      offset: 0,
      order: "time"
    })
    .then(res=>{
         const {data} = res.data
         reduxStore.dispatch(changGoldList(data))
        
         return  { GOLD_LIST: (reduxStore.getState())['GOLD_LIST']}
    })  
  }

  componentDidMount () {
